import React, { useEffect, useState } from "react";
import docImg from "../images/doctor.jpg"
import { JsonFunction } from "react-router-dom";

const Card = ({ name, username, id }) => {
  const[favorito, setFavorito] = useState();
  const storedFavs = JSON.parse(localStorage.getItem('favs')) || []


  const newFav = () => {
    const currentFav = {
    id: `${id}`,
    name: `${name}`,
    username: `${username}`,
    img: `${docImg}`
  }
    if(storedFavs === []){
      storedFavs = currentFav
    }
    else{
      storedFavs = [...storedFavs, currentFav]
    }
    console.log(storedFavs)
  }


  return (
    <div className="card">
        <img className="docImg" src={docImg}></img>
        {/* En cada card deberan mostrar en name - username y el id */}
        <h1>{name}</h1>
        <h3>{username}</h3>
        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button onClick={newFav} className="favButton">Add fav</button>
    </div>
  );
};

export default Card;
