import React, { useEffect, useState } from "react";
import docImg from "../images/doctor.jpg"
import { JsonFunction } from "react-router-dom";

const Card = ({ name, username, id }) => {

  const[data, setData] = useState([]);
  let favoritos = localStorage.getItem('favs') || []

  const addFav = () =>{
    const dentFav = {
      id: `${id}`,
      name: `${name}`,
      username: `${username}`,
      img: `${docImg}`
    }

    setData({...favoritos, dentFav})

    favoritos = localStorage.setItem('favs', JSON.stringify(data))

  }

  useEffect(()=>{
    console.log(data)
  },[data])


  return (
    <div className="card">
        <img className="docImg" src={docImg}></img>
        {/* En cada card deberan mostrar en name - username y el id */}
        <h1>{name}</h1>
        <h3>{username}</h3>
        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button onClick={addFav} className="favButton">Add fav</button>
    </div>
  );
};

export default Card;
