import React, { useEffect, useState } from "react";


const Form = () => {
  //Aqui deberan implementar el form completo con sus validaciones

  const [contacto, setContacto] = useState({
    nombre: "",
    email: ""
  })

  const [validation, setValidaton] = useState('')

  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const handleSubmit = (event) => {
    event.preventDefault()
    if (contacto.nombre.length>5 && emailRegex.test(contacto.email)){
      setValidaton(true)
    }
    else {
      setValidaton(false)
    }
  }

  let mensaje = ''
  let success = `Gracias ${contacto.nombre}, te contactaremos cuando antes vía mail`;
  const notValid = "Por favor verifique su información nuevamente";

  useEffect(()=>{
    if (validation === true){
      mensaje = success
    }
    else if (validation === false) {
      mensaje = notValid
    }
  },[])


  return (
    <div>
      <form>
        <label>Nombre Completo</label>
        <input type="text" placeholder="nombre apellido" onChange={(e) => setContacto({...contacto, nombre: e.target.value})}></input>
        <label>Email</label>
        <input type="email" placeholder="usuario@mail.com" onChange={(e) => setContacto({...contacto, email: e.target.value})}></input>
        <button onClick={handleSubmit}>ENVIAR</button>
      </form>
      {mensaje}
    </div>
  );
};

export default Form;
